    public void doTasks() {
        AtomicInteger atomicInteger = new AtomicInteger(3);
        try {
            while (atomicInteger.get() > 0 && !Thread.currentThread().isInterrupted()) {
                boolean hasTask = query(atomicInteger);
                if (!hasTask) {
                    atomicInteger.set(0); // Âº∫Âà∂ÈáçÁΩÆËÆ°Êï∞Âô®
                    Thread.currentThread().interrupt(); // ‰º†Êí≠‰∏≠Êñ≠Áä∂ÊÄÅ
                    Log.other(displayName + "‰∏ªÂä®ÁªàÊ≠¢‰ªªÂä°Â§ÑÁêÜÊµÅÁ®ã");
                }
                TimeUtil.sleep(5000);
            }
        } catch (JSONException e) {
            throw new RuntimeException("‰ªªÂä°Â§ÑÁêÜÂ§±Ë¥•", e);
        }
    }

    private Boolean query(AtomicInteger atomicInteger) throws JSONException {
        boolean hasTask = false;
        try {
            String results = queryTasks();
            JSONObject result = new JSONObject(results);
            if (result == null || (result.optInt("resultCode") != 200)) {
                Log.other(displayName + "Êü•ËØ¢ËßÜÈ¢ëÊÉÖÂÜµ‰∏∫nullÊàñËÄÖÁΩëÁªúÈîôËØØ");
                Log.error(displayName + "ÈîôËØØ:" + result);
                int remainingRetries = atomicInteger.decrementAndGet();
                if (remainingRetries <= 0) {
                    Log.other(displayName + "ÈáçËØïÊ¨°Êï∞Â∑≤Áî®Â∞ΩÔºåÂÅúÊ≠¢ÈáçËØï");
                    return false; // ÂÅúÊ≠¢ÈáçËØï
                }
                return true; // ÁªßÁª≠ÈáçËØï
            }
            JSONArray taskList = result.getJSONArray("taskList");

            for (int i = 0; i < taskList.length(); i++) {
                JSONObject task = taskList.getJSONObject(i);

                String taskType = task.getString("taskType");
                if (!"radicalRed".equals(taskType)) {
                    Log.other(displayName + "Ë∑≥ËøáÈùûÁõÆÊ†á‰ªªÂä°Á±ªÂûã: " + taskType);
                    continue;
                }
                // Â§ÑÁêÜÂ∑≤ÂÆåÊàê‰ªªÂä°
                if (task.optBoolean("completed")) {
                    Log.other(displayName + "‰ªäÊó•Âà∑ËßÜÈ¢ëÂ∑≤ÁªèÂÆåÊàê");
                    Status.setFlagToday(CompletedKeyEnum.VIDEOCOMPLETE.name());
                    return false;
                }
                // Â§ÑÁêÜÊú™ÂÆåÊàê‰ªªÂä°
                if (!task.optBoolean("completed")) {
                    JSONObject taskData = task.getJSONObject("taskData");
                    // ÊâìÂç∞ËøõÂ∫¶‰ø°ÊÅØ
                    if (taskData.has("progress")) {
                        double progress = taskData.getDouble("progress") * 100;
                        Log.other(displayName + "‰ªªÂä°ËøõÂ∫¶: " + String.format("%.1f%%", progress));
                        if (progress == 1) {
                            walletReward();
                        }
                    } else if (taskData.has("curAmount") && taskData.has("totalAmount")) {
                        double cur = taskData.getDouble("curAmount");
                        double total = taskData.getDouble("totalAmount");
                        Log.other(displayName + "ÈáëÈ¢ùËøõÂ∫¶: " + cur + "/" + total);
                    }
                    // ÊâßË°åÂ•ñÂä±È¢ÜÂèñ
                    claimReward(task);
                }
            }
            hasTask = true;
        } catch (JSONException e) {
            Log.other(displayName + "ËøêË°åÈîôËØØ:" + e);
            Log.error(displayName + "ËøêË°åÈîôËØØ:" + e);
            int remainingRetries = atomicInteger.decrementAndGet();
            if (remainingRetries <= 0) {
                Log.other(displayName + "ÈáçËØïÊ¨°Êï∞Â∑≤Áî®Â∞ΩÔºåÂÅúÊ≠¢ÈáçËØï");
                return false; // ÂÅúÊ≠¢ÈáçËØï
            }
            return true; // ÁªßÁª≠ÈáçËØï
        }
        return hasTask;
    }

